package main

import (
	"encoding/base64"
	"fmt"
	"os"
)

var sign_cert = ""
var enc_cert = ""

func main() {

	fmt.Println(os.Getwd())
	bytes, err := base64.StdEncoding.DecodeString(sign_cert)
	if err != nil {
		fmt.Printf("err:%s", err)
	}
	err = os.WriteFile("sign_cert.pem", bytes, 0755)
	if err != nil {
		fmt.Printf("err:%s", err)
	}

	//openssl x509 -in sign_cert.pem -noout -text

	bytes, err = base64.StdEncoding.DecodeString(enc_cert)
	if err != nil {
		fmt.Printf("err:%s", err)
	}
	err = os.WriteFile("enc_cert.pem", bytes, 0755)
	if err != nil {
		fmt.Printf("err:%s", err)
	}
}
